package com.learn.java;

import java.util.Scanner;

public class SumAdd {

	public static void main(String[] args) {
		
		Scanner sc = new Scanner(System.in);
        int n = sc.nextInt();
        for(int t = 0; t < n; t++) {
            int a = sc.nextInt();
            int b = sc.nextInt();
            System.out.println(a+b);
        }
		// TODO Auto-generated method stub
        
	}

}
